import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { CloudUpload, List } from '@mui/icons-material';
import '../styles/theme.css';

const Dashboard = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFiles();
  }, []);

  const fetchFiles = async () => {
    try {
      const response = await fetch('http://yourbackend.com/dashboard.php');
      const data = await response.json();

      if (data.success) {
        setFiles(data.files);
      }
      setLoading(false);
    } catch (error) {
      console.error('Error fetching files:', error);
      setLoading(false);
    }
  };

  const handleFileDelete = async (fileId) => {
    try {
      const response = await fetch('http://yourbackend.com/dashboard.php', {
        method: 'DELETE',
        body: JSON.stringify({ id: fileId })
      });

      const result = await response.json();
      if (result.success) {
        fetchFiles(); // Refresh file list
      }
    } catch (error) {
      console.error('Error deleting file:', error);
    }
  };

  return (
    <div className="hero-container">
      <div className="hero-content">
        <h1 className="hero-title">
          Storify Dashboard
        </h1>

        <h2 className="hero-subtitle">
          {files.length === 0 
            ? "No files uploaded yet. Start organizing your files!" 
            : `You have ${files.length} file${files.length !== 1 ? 's' : ''} uploaded`}
        </h2>

        <Link to="/upload" className="upload-link">
          <button className="upload-button">
            <CloudUpload />
            Upload Files
          </button>
        </Link>

        {files.length > 0 && (
          <div className="file-list">
            {files.map(file => (
              <div key={file.id} className="file-item">
                <div className="file-info">
                  <List /> {file.name}
                  <span className="file-details">
                    {file.size} bytes | {file.type}
                  </span>
                </div>
                <button 
                  onClick={() => handleFileDelete(file.id)} 
                  className="delete-button"
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
