// theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          margin: '8px',
        },
      },
    },
  },
  typography: {
    h1: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
    },
    h2: {
      fontSize: '1.5rem',
      color: '#555',
    },
  },
  palette: {
    primary: {
      main: '#6c63ff',
    },
  },
  // Define global styles or reusable styles
  globalStyles: {
    dashboardContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      textAlign: 'center',
      padding: '16px',
    },
    dashboardTitle: {
      marginBottom: '16px',
    },
    dashboardSubtitle: {
      marginBottom: '32px',
    },
  },
});

export default theme;
