import React from 'react';
import {
  AppBar,
  Toolbar,
  Button,
  Box,
  Container,
  IconButton,
  useMediaQuery,
  useTheme,
  Menu,
  MenuItem,
} from '@mui/material';
import { Menu as MenuIcon, Info as InfoIcon } from '@mui/icons-material';
import { Link, useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';
import './navbar.css'; // Importando o CSS

const Navbar = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const location = useLocation();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const isActive = (path) => location.pathname === path;

  const navigationItems = [
    { path: '/upload', label: 'Upload File' },
    { path: '/files', label: 'My Files' },
  ];

  return (
    <AppBar className="styled-appbar" position="fixed">
      <Container maxWidth="lg">
        <Toolbar disableGutters>
          <Link to="/" className="logo-container">
            <img src={logo} alt="Storify Logo" className="logo" />
          </Link>

          <Box sx={{ flexGrow: 1 }} />
          {isMobile ? (
            <>
              <IconButton
                size="large"
                edge="end"
                color="primary"
                aria-label="menu"
                onClick={handleMenu}
              >
                <MenuIcon />
              </IconButton>
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleClose}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
              >
                {navigationItems.map((item) => (
                  <MenuItem
                    key={item.path}
                    component={Link}
                    to={item.path}
                    onClick={handleClose}
                    selected={isActive(item.path)}
                  >
                    {item.label}
                  </MenuItem>
                ))}
              </Menu>
            </>
          ) : (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              {navigationItems.map((item) => (
                <Button
                  key={item.path}
                  component={Link}
                  to={item.path}
                  variant={isActive(item.path) ? "contained" : "text"}
                  color="primary"
                  className={`nav-button ${isActive(item.path) ? 'active' : ''}`}
                >
                  {item.label}
                </Button>
              ))}
              <Button
                component={Link}
                to="/about"
                variant={isActive('/about') ? "contained" : "text"}
                color="primary"
                className={`nav-button ${isActive('/about') ? 'active' : ''}`}
                startIcon={<InfoIcon />}
              >
                About
              </Button>
            </Box>
          )}
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Navbar;
