import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { CloudUpload } from '@mui/icons-material';
import '../styles/theme.css';

const Dashboard = () => {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    fetchFiles();
  }, []);

  const fetchFiles = async () => {
    try {
      const response = await fetch('http://yourbackend.com/dashboard.php');
      const data = await response.json();
      if (data.success) {
        setFiles(data.files);
      }
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  const handleFileDelete = async (fileId) => {
    try {
      const response = await fetch('http://yourbackend.com/dashboard.php', {
        method: 'DELETE',
        body: JSON.stringify({ id: fileId })
      });
      const result = await response.json();
      if (result.success) {
        fetchFiles();
      }
    } catch (error) {
      console.error('Error deleting file:', error);
    }
  };

  return (
    <div className="hero-container">
      <div className="hero-content">
        <h1 className="hero-title">Welcome to Storify</h1>
        <h2 className="hero-subtitle">
          {files.length === 0 
            ? "Upload, organize and access your files from anywhere." 
            : `You have ${files.length} file${files.length !== 1 ? 's' : ''} uploaded`}
        </h2>
        <Link to="/upload" className="upload-link">
          <button className="upload-button">
            <CloudUpload />
            Start Uploading
          </button>
        </Link>
        {files.length > 0 && (
          <div className="file-list">
            {files.map(file => (
              <div key={file.id} className="file-item">
                <span>{file.name}</span>
                <button onClick={() => handleFileDelete(file.id)}>Delete</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
